mapper.select:
SELECT count(*) FROM sys_permission p,sys_rp rp WHERE rp.role_id=#{roleId} AND rp.permission_id=p.permission_id AND p.resource_id in
#{item}
AND P.privilege=#{permission}

SELECT r.resource_id as resourceId FROM sys_resource r INNER JOIN sys_permission p ON p.resource_id = r.resource_id INNER JOIN sys_rp rp ON p.permission_id = rp.permission_id
AND rp.role_id in
#{item.roleId}
AND r.resource_type = #{q.resourceType}
AND p.privilege = #{q.privilege}
order by r.parent_id,r.sequence_num
